障部&lt;/span&gt;&lt;span style=&quot;line-height:2;&quot;&gt;&amp;nbsp;&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:2;&quot;&gt; \r\n&lt;div style=&quot;text-align:left;&quot;&gt;\r\n	&lt;span style=&quot;line-height:2;&quot;&gt;中国残联&lt;/span&gt;&lt;span style=&quot;line-height:2;&quot;&gt;&amp;nbsp;&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;/span&gt;&lt;span style=&quot;line-height:2;&quot;&gt; \r\n&lt;div style=&quot;text-align:left;&quot;&gt;\r\n	&lt;span style=&quot;line-height:2;&quot;&gt;2016年6月20日&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;/span&gt; \r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;p&quot; align=&quot;right&quot; style=&quot;margin-left:0.0000pt;text-align:right;&quot;&gt;\r\n	&lt;span style=&quot;line-height:2;&quot;&gt;&amp;nbsp;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;p&quot; align=&quot;right&quot; style=&quot;margin-left:0.0000pt;text-align:right;&quot;&gt;\r\n	&lt;span style=&quot;line-height:2;&quot;&gt;&amp;nbsp;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;p&quot; align=&quot;justify&quot; style=&quot;margin-left:0.0000pt;text-align:justify;&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:2;&quot;&gt;附件&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;p&quot; align=&quot;center&quot; style=&quot;margin-left:0.0000pt;text-align:center;&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:2;&quot;&gt;重点任务分工及进度安排表&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;table class=&quot;MsoNormalTable&quot; align=&quot;center&quot; style=&quot;width:426.1pt;&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td width=&quot;60&quot; valign=&quot;center&quot; style=&quot;border:1.0000pt solid windowtext;&quot;&gt;\r\n				&lt;p class=&quot;p&quot; align=&quot;center&quot; style=&quot;margin-left:0.0000pt;text-align:center;&quot;&gt;\r\n					&lt;b&gt;&lt;span style=&quot;font-family:宋体;font-size:14pt;line-height:2;&quot;&gt;序号&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-fa